mapper.select:
SELECT vm.id AS id, vm.name AS name, vm.ip AS ip, vm.cpu AS cpu, vm.memory AS memory, vm.datadisk AS datadisk, vm.os AS os, vm.status AS status, cp.name AS platform, hm.ip AS hostip, date(vm.duedate) AS duedate FROM t_virtualmachine vm JOIN t_cloudplatform cp ON vm.platform = cp.id JOIN t_hostmachine hm ON vm.hostmachine_id = hm.id WHERE 1 = 1
and vm.user=#{pd.FROM_USERNAME}
ORDER BY vm.gmt_modified DESC

mapper.update:
UPDATE
	t_virtualmachine t
SET
	t.status=#{status}
WHERE
	t.id=#{id}
