mapper.select:
select a.id, a.name, a.uuid from t_virtualmachine a where a.type!='kvm'
and a.user=#{applyUserId}
and exists ( SELECT 1 FROM act_hi_procinst b INNER JOIN act_hi_actinst c on c.PROC_INST_ID_=b.PROC_INST_ID_ where exists (select 1 from t_usergroup_user_map d where d.USERNAME=c.ASSIGNEE_ and FIND_IN_SET(d.usergroup_id, #{audit})) and b.BUSINESS_KEY_=a.appNo )

select a.id, a.name, a.uuid from t_hostmachine a inner join t_virtualmachine b on b.hostmachine_id=a.id where a.type=='kvm'
and b.user=#{applyUserId}
and exists ( SELECT 1 FROM act_hi_procinst c INNER JOIN act_hi_actinst d on d.PROC_INST_ID_=c.PROC_INST_ID_ where exists (select 1 from t_usergroup_user_map e where e.USERNAME=d.ASSIGNEE_ and FIND_IN_SET(e.usergroup_id, #{audit})) and c.BUSINESS_KEY_=b.appNo )

select a.id, a.name, a.uuid from t_virtualmachine a where a.type=='kvm'
and a.user=#{applyUserId}
and exists ( SELECT 1 FROM act_hi_procinst b INNER JOIN act_hi_actinst c on c.PROC_INST_ID_=b.PROC_INST_ID_ where exists (select 1 from t_usergroup_user_map d where d.USERNAME=c.ASSIGNEE_ and FIND_IN_SET(d.usergroup_id, #{audit})) and b.BUSINESS_KEY_=a.appNo )
