mapper.select:
select count(1) from t_virtualmachine a where a.type!='kvm'
and a.user=#{applyUserId}
and exists ( SELECT 1 FROM act_hi_procinst b INNER JOIN act_hi_actinst c on c.PROC_INST_ID_=b.PROC_INST_ID_ where exists (select 1 from t_usergroup_user_map d where d.USERNAME=c.ASSIGNEE_ and FIND_IN_SET(d.usergroup_id, #{audit})) and b.BUSINESS_KEY_=a.appNo )

select count(1) from t_virtualmachine a where a.type='kvm'
and a.user=#{applyUserId}
and exists ( SELECT 1 FROM act_hi_procinst b INNER JOIN act_hi_actinst c on c.PROC_INST_ID_=b.PROC_INST_ID_ where exists (select 1 from t_usergroup_user_map d where d.USERNAME=c.ASSIGNEE_ and FIND_IN_SET(d.usergroup_id, #{audit})) and b.BUSINESS_KEY_=a.appNo )

select count(1) from t_project a where 1=1
and a.USERNAME=#{applyUserId}
and exists (select 1 from sys_user b where b.DEPARTMENT_ID=a.DEPARTMENT_ID and b.USERNAME=#{applyUserId})

select count(1) from cmp_workorder a where 1=1
and a.applyUserId=#{applyUserId}
and exists (select 1 from sys_user b where b.DEPARTMENT_ID=a.deptId and b.USERNAME=#{applyUserId})
