mapper.select:
select a.id, a.username, a.type operType, b.dictValue operTypeName, a.opt_object, a.opt_status, c.dictValue optStatusName, a.ip, a.detail, DATE_FORMAT(a.gmt_create, '%Y-%m-%d %H:%i:%s') gmt_create, a.gmt_modified from a left join cmp_dict b on b.dictType='oper_type' and b.dictCode=a.type left join cmp_dict c on c.dictType='oper_type' and c.dictCode=a.opt_status where a.username=#{pd.applyUserId}
and DATE_FORMAT(a.gmt_create, '%Y-%m-%d') >= #{pd.beginDate}
and #{pd.endDate} >= DATE_FORMAT(a.gmt_create, '%Y-%m-%d')
order by a.gmt_create desc
