mapper.select:
select s.id, s.name, s.type, s.status, s.ip, s.cpu, s.memory, s.localdisk, s.devicenum, s.duedate, s.account, s.password_ssh, s.gmt_create, s.gmt_modified, s.cluster_id, s.port, s.cpf_id, s.datacenter_id, s.uuid, s.environment_id, c.name AS cluster_name from s, c where s.cluster_id=c.id
and ( s.name LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and id in ${pd.item}
and s.type = #{pd.type}
order by s.gmt_modified desc

select s.id, s.name, s.type, s.status, s.ip, s.cpu, s.memory, s.localdisk, s.devicenum, s.duedate, s.account, s.password_ssh, s.gmt_create, s.gmt_modified, s.cluster_id, s.cpf_id, s.datacenter_id, s.port, s.uuid from s where 1 = 1
and ( s.name LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and id in ${pd.item}
and s.type = #{pd.type}
order by s.gmt_modified desc
