mapper.select:
SELECT a.id, a.name, a.status, a.ip, a.hostmachine_id, b.name as hostmachine_name from a, b WHERE a.hostmachine_id=b.id AND a.type=#{pd.type}
and a.hostmachine_id=#{pd.hostmachine_id}
ORDER BY a.gmt_modified desc

mapper.update:
UPDATE
	t_virtualmachine t
SET
	t.status=#{status}
WHERE
	t.id=#{id}
